import math

def solve():
    l, r = map(int, input().split())

    # If l == r, check if it's coprime with itself
    if l == r:
        return 1 if math.gcd(l, l) == 1 else 0

    # Count of minimal coprime segments
    count = 0

    # For each possible length of segment
    for length in range(1, r - l + 1):
        # For each possible starting point
        for start in range(l, r - length + 2):
            end = start + length - 1

            # Check if segment [start, end] is coprime
            if math.gcd(start, end) == 1:
                # Check if it's minimal
                is_minimal = True

                # Check all subsegments
                for sub_start in range(start, end + 1):
                    for sub_end in range(sub_start, end + 1):
                        # Skip the segment itself
                        if sub_start == start and sub_end == end:
                            continue

                        # If a subsegment is coprime, then [start, end] is not minimal
                        if math.gcd(sub_start, sub_end) == 1:
                            is_minimal = False
                            break

                    if not is_minimal:
                        break

                if is_minimal:
                    count += 1

    return count

def main():
    t = int(input())
    for _ in range(t):
        print(solve())

if __name__ == "__main__":
    main()
