def solve():
    n, b, c = map(int, input().split())

    # Check if array is already a permutation
    if b == 0:
        # All elements are equal to c
        if c == 0 and n == 1:
            return 0  # [0] is a valid permutation
        elif 0 <= c < n:
            # We need to replace all elements except one
            return n - 1
        else:
            return -1  # Impossible

    # Check if array contains all distinct elements
    if b >= n:
        return -1  # Elements grow too fast, can't form a permutation

    # Calculate the initial MEX
    # For array [c, b+c, 2b+c, ...]

    # If c > 0, the MEX is 0
    # If c = 0, the MEX is the first missing value

    operations = 0

    if c > 0:
        # MEX is 0, we need to replace the maximum element with 0
        operations += 1

        # After replacing the maximum, we need to check if we have a permutation
        # The maximum was (n-1)*b + c, now replaced with 0
        # We need to check if the remaining elements form a permutation with 0

        # If b = 1, after replacing the maximum, we have [c, c+1, ..., c+n-3, c+n-2, 0]
        # For this to be a permutation, we need c = 1
        if b == 1:
            if c == 1:
                return operations  # We have [1, 2, ..., n-1, 0]
            else:
                # We need to replace more elements
                max_val = (n-2)*b + c  # New maximum after first replacement

                # We need to replace elements until we have [0, 1, ..., n-1]
                # Each replacement adds a new value to our set
                needed_replacements = n - 1  # We already have 0

                # Check if we can get all values from 0 to n-1
                if c <= n-1 and c + (n-2)*b >= n-1:
                    # We can form a permutation
                    missing_values = set(range(n)) - set(range(c, c + (n-1)*b, b))
                    missing_values.discard(0)  # We already have 0
                    return operations + len(missing_values)
                else:
                    return -1

        # If b > 1, we can't form a permutation because the gaps between elements are too large
        if b > 1:
            # Check if we can form a permutation
            # We have [c, c+b, c+2b, ..., c+(n-2)b, 0]

            # For this to be a permutation, we need all values from 0 to n-1
            # If b > 1, we'll have gaps in our sequence

            # Check if we can get all values from 0 to n-1
            if c <= n-1 and c + (n-2)*b >= n-1:
                # We can potentially form a permutation
                # Count how many operations we need
                current_elements = set([0])  # We already have 0
                for i in range(n-1):
                    val = c + i*b
                    if val < n:
                        current_elements.add(val)

                return operations + (n - len(current_elements))
            else:
                return -1

    else:  # c = 0
        # Array is [0, b, 2b, 3b, ...]

        # If b = 1, we already have a permutation [0, 1, 2, ...]
        if b == 1:
            return 0

        # If b > 1, we have [0, b, 2b, ...] with gaps
        # Check if we can form a permutation
        if b < n:
            # We can potentially form a permutation
            # Count how many operations we need
            current_elements = set()
            for i in range(n):
                val = i*b
                if val < n:
                    current_elements.add(val)

            return n - len(current_elements)
        else:
            return -1

