        return 0

    # Try to make all elements equal to each possible value
    min_ops = float('inf')

    for target in set(a):
        ops = 0
        temp_a = a.copy()

        while len(temp_a) > 1 and ops < n:
            m = len(temp_a)
            found = False

            for i in range(m):
                next_i = (i + 1) % m

                if temp_a[i] <= temp_a[next_i]:
                    # If one of the elements is already the target, delete the other
                    if temp_a[i] == target:
                        temp_a.pop(next_i if next_i != 0 else 0)
                    elif temp_a[next_i] == target:
                        temp_a.pop(i)
                    # Otherwise delete the smaller one
                    elif temp_a[i] < temp_a[next_i]:
                        temp_a.pop(i)
                    else:  # They are equal but not the target
                        temp_a.pop(i)

                    ops += 1
                    found = True
                    break

            if not found:
                ops = float('inf')
                break

        if all(x == target for x in temp_a):
