def solve(n, k, p):
    if n == 1:
        return 1

    # Calculate the number of nodes at each level
    level_nodes = [1]
    for i in range(1, n):
        level_nodes.append(level_nodes[-1] * 2)

    # Initialize DP array
    dp = [[0] * (k + 1) for _ in range(n)]
    dp[0][0] = 1

    # Fill DP array
    for i in range(1, n):
        for j in range(k + 1):
            for x in range(j + 1):
                # Number of ways to distribute x operations to the left subtree
                # and (j - x) operations to the right subtree
                left_ways = pow(level_nodes[i - 1], x, p)
                right_ways = pow(level_nodes[i - 1], j - x, p)

                # If the left subtree gets more operations than the right subtree,
                # the heap is deterministic
                if x > j - x:
                    dp[i][j] = (dp[i][j] + dp[i-1][0] * left_ways * right_ways) % p

    # Sum up all possible ways
    result = 0
    for j in range(k + 1):
        result = (result + dp[n-1][j]) % p

    return result

