        on_stack[i][j] = True

        ni, nj = get_next(i, j)

        # If next cell is outside the grid, this path doesn't lead to a cycle
        if ni == -1:
            on_stack[i][j] = False
            return False

        # If next cell is already on the stack, we found a cycle
        if on_stack[ni][nj]:
            is_in_cycle[i][j] = True
            on_stack[i][j] = False
            return True

        # If next cell is already known to be in a cycle or lead to a cycle
        if is_in_cycle[ni][nj] or leads_to_cycle[ni][nj]:
            leads_to_cycle[i][j] = True
            on_stack[i][j] = False
            return True

        # If next cell is already visited but not in a cycle
        if visited[ni][nj]:
            on_stack[i][j] = False
            return False

        # Explore next cell
        if dfs(ni, nj):
            if is_in_cycle[ni][nj]:
                is_in_cycle[i][j] = True
            else:
                leads_to_cycle[i][j] = True
            on_stack[i][j] = False
            return True

        on_stack[i][j] = False
        return False

    # Run DFS from each unvisited cell
    for i in range(n):
        for j in range(m):
            if not visited[i][j]:
                dfs(i, j)

    # Count cells that are in cycles or lead to cycles
    count = 0
    for i in range(n):
        for j in range(m):
            if is_in_cycle[i][j] or leads_to_cycle[i][j]:
                count += 1

    return count

