        for i in range(n):
            for j in range(i+1, n):
                g = math.gcd(a[i], a[j])
                if g > 1:
                    # If a[i] is divisible by g, move it to group g
                    if a[i] % g == 0 and a[i] not in gcd_groups[g]:
                        gcd_groups[a[i]].remove(a[i])
                        gcd_groups[g].append(a[i])
                    # If a[j] is divisible by g, move it to group g
                    if a[j] % g == 0 and a[j] not in gcd_groups[g]:
                        gcd_groups[a[j]].remove(a[j])
                        gcd_groups[g].append(a[j])

        # For each group, we can only take one number
        result = []
        for group in gcd_groups.values():
            if group:
                result.append(group[0])

        # Special case handling for prime numbers
        prime_candidates = []
        for num in a:
