            for i in range(2, int(math.sqrt(num)) + 1):
                if num % i == 0:
                    is_prime = False
                    break
            if is_prime and num > 1:
                prime_candidates.append(num)

        # If we have prime numbers, they form a linearly independent set
        if len(prime_candidates) > len(result):
            result = prime_candidates

        # Handle the case where we need to check for linear independence more carefully
        if len(result) < min(3, n):
            # Try to find a set of size 2 or 3 that is linearly independent
