def calculate_f(arr, k):
    n = len(arr)

    # If there's already a consecutive subarray of length k with the same value, return 0
    for i in range(n - k + 1):
        if all(arr[i] == arr[i+j] for j in range(1, k)):
            return 0

    # For any array of length >= 2k-1, we need at most k-1 operations
    if n >= 2*k - 1:
        return k - 1

    # For arrays of length < 2k-1, we need to find the most frequent element
    freq = {}
    for num in arr:
        freq[num] = freq.get(num, 0) + 1

    max_freq = max(freq.values())

    # If the most frequent element appears at least n-k+1 times, we need n-max_freq operations
    if max_freq >= n - k + 1:
        return n - max_freq

    # Otherwise, we need k-1 operations
    return k - 1

