def solve():
    s = input()
    n = len(s)

    # dp[i] = maximum sum achievable ending at position i
    dp = [0] * (n + 1)

    for i in range(n):
        if s[i] == '+':
            continue

        # Try to form a valid expression ending at position i
        for j in range(i, -1, -1):
            if j > 0 and s[j-1] != '+':
                continue

            # Check if this is a valid start of an expression
            if j == 0 or s[j-1] == '+':
                # Find the + sign
                plus_pos = -1
                for k in range(j, i+1):
                    if s[k] == '+':
                        plus_pos = k
                        break

                if plus_pos != -1:
                    # We found a valid expression j...i
                    left = int(s[j:plus_pos])
                    right = int(s[plus_pos+1:i+1])

                    # Update dp
                    dp[i+1] = max(dp[i+1], dp[j] + left + right)

            # If we've reached a + sign or the beginning, we can't go further back
            if j == 0 or s[j-1] == '+':
                break

    return dp[n]

