def solve():
    n, m = map(int, input().split())
    messengers = list(map(int, input().split()))

    # Build the graph
    graph = [[] for _ in range(n)]
    for _ in range(m):
        u, v = map(int, input().split())
        graph[u-1].append(v-1)

    # Find strongly connected components using Kosaraju's algorithm
    def dfs1(node, visited, stack):
        visited[node] = True
        for neighbor in graph[node]:
            if not visited[neighbor]:
                dfs1(neighbor, visited, stack)
        stack.append(node)

    def dfs2(node, visited, component, transpose):
        visited[node] = True
        component.append(node)
        for neighbor in transpose[node]:
            if not visited[neighbor]:
                dfs2(neighbor, visited, component, transpose)

    # Create transpose graph
    transpose = [[] for _ in range(n)]
    for i in range(n):
        for j in graph[i]:
            transpose[j].append(i)

    # First DFS to fill the stack
    visited = [False] * n
    stack = []
    for i in range(n):
        if not visited[i]:
            dfs1(i, visited, stack)

    # Second DFS to find SCCs
    visited = [False] * n
    sccs = []
    while stack:
        node = stack.pop()
        if not visited[node]:
            component = []
            dfs2(node, visited, component, transpose)
            sccs.append(component)

    # Create a condensation graph
    scc_graph = [[] for _ in range(len(sccs))]
    scc_id = {}
    for i, scc in enumerate(sccs):
        for node in scc:
            scc_id[node] = i

    for i in range(n):
        for j in graph[i]:
            if scc_id[i] != scc_id[j]:
                scc_graph[scc_id[i]].append(scc_id[j])

    # Remove duplicates in scc_graph
    scc_graph = [list(set(neighbors)) for neighbors in scc_graph]

    # Find source SCCs (those with no incoming edges)
    in_degree = [0] * len(sccs)
    for i in range(len(sccs)):
        for j in scc_graph[i]:
            in_degree[j] += 1

    source_sccs = [i for i in range(len(sccs)) if in_degree[i] == 0]

    # Count messengers in each SCC
    scc_messengers = [0] * len(sccs)
    for i, scc in enumerate(sccs):
        for node in scc:
            scc_messengers[i] += messengers[node]

    # Check if any SCC has no messengers
    for i in range(len(sccs)):
        if scc_messengers[i] == 0:
            # Check if this SCC can be reached from any SCC with messengers
            reachable = False
            for j in range(len(sccs)):
                if scc_messengers[j] > 0:
                    # Check if there's a path from j to i
                    visited = [False] * len(sccs)

                    def dfs_check(node):
                        if node == i:
                            return True
                        visited[node] = True
                        for neighbor in scc_graph[node]:
                            if not visited[neighbor]:
                                if dfs_check(neighbor):
                                    return True
                        return False

                    if dfs_check(j):
                        reachable = True
                        break

            if not reachable:
                return -1

    # Count how many source SCCs need plans
    plans_needed = 0
    for scc_idx in source_sccs:
        if scc_messengers[scc_idx] > 0:
            plans_needed += 1
        else:
            return -1  # If a source SCC has no messengers, it's impossible

    return plans_needed

