def solve(x, m):
    # Case 1: y is divisible by x
    count_case1 = m // x

    # Case 2: x is divisible by y
    count_case2 = 0
    for y in range(1, min(m + 1, x + 1)):
        if x % y == 0 and (x ^ y) % y == 0:
            count_case2 += 1 + (m - y) // y

    # Case 3: Neither x is divisible by y nor y is divisible by x, but (x^y) is divisible by either x or y
    count_case3 = 0
    for y in range(1, min(m + 1, x)):
        if x % y != 0 and y % x != 0:
            if (x ^ y) % x == 0 or (x ^ y) % y == 0:
                count_case3 += 1

    # Avoid double counting
    return count_case1 + count_case2 - count_case3

