    # Calculate the number of arrays with maximum value exactly i
    max_val_count = [0] * (m + 1)
    max_val_count[1] = 1  # Only the array [1]

    for i in range(2, m + 1):
        # For max value i, we can have any sequence of numbers from 1 to i
        # with at least one occurrence of i
        max_val_count[i] = pow(i, i, MOD) - pow(i - 1, i, MOD)
        max_val_count[i] %= MOD

    # Calculate f(k) for each possible length k
    f_values = {}

    # For each possible maximum value
    for max_val in range(1, m + 1):
        # Find all divisors of max_val
        divisors = []
        for i in range(1, int(max_val**0.5) + 1):
            if max_val % i == 0:
                divisors.append(i)
                if i != max_val // i:
                    divisors.append(max_val // i)

        # For each length from 1 to max_val
        for length in range(1, max_val + 1):
            # Calculate f(length) for arrays with maximum value max_val
            gcd_val = max_val

            # Add this contribution to f_values
            if length not in f_values:
                f_values[length] = [0] * (m + 1)
            f_values[length][gcd_val] = (f_values[length][gcd_val] + max_val_count[max_val]) % MOD

    # Count good arrays
    good_arrays = 0

    # For each possible length
    for length in f_values:
        # Count arrays with this length that have unique f values
        unique_f_values = set()
        for gcd_val in range(1, m + 1):
            if f_values[length][gcd_val] > 0:
                if gcd_val not in unique_f_values:
