    # Calculate prefix sums for each row
    prefix_sums = []
    for row in profits:
        prefix = [0]
        for val in row:
            prefix.append(prefix[-1] + val)
        prefix_sums.append(prefix)

    # For day 1, find the best subarray
    best_subarrays_day1 = []
    for i in range(1, m + 1):
        for j in range(i, m + 1):
            profit = prefix_sums[0][j] - prefix_sums[0][i - 1]
            best_subarrays_day1.append((i, j, profit))

    # Sort by profit
    best_subarrays_day1.sort(key=lambda x: x[2], reverse=True)

    # For each day after day 1
    max_profit = float('-inf')
    for first_day_choice in best_subarrays_day1:
        start1, end1, profit1 = first_day_choice

        # Initialize dp array for the remaining days
        dp = [profit1] + [float('-inf')] * (n - 1)

        prev_start, prev_end = start1, end1

        # For each subsequent day
        for day in range(1, n):
            best_profit_for_day = float('-inf')

            # Try all possible subarrays for this day
            for start in range(1, m + 1):
                for end in range(start, m + 1):
                    # Check if there's at least one common drink and one different drink
                    has_common = False
                    has_different = False

                    for drink in range(start, end + 1):
                        if prev_start <= drink <= prev_end:
                            has_common = True
                        else:
                            has_different = True

                    if has_common and has_different:
                        profit = prefix_sums[day][end] - prefix_sums[day][start - 1]
                        best_profit_for_day = max(best_profit_for_day, profit)

            if best_profit_for_day != float('-inf'):
                dp[day] = dp[day - 1] + best_profit_for_day

                # Find the best subarray for this day to use for the next day
                best_next_start, best_next_end = 0, 0
                best_next_profit = float('-inf')

                for start in range(1, m + 1):
                    for end in range(start, m + 1):
                        has_common = False
                        has_different = False

                        for drink in range(start, end + 1):
                            if prev_start <= drink <= prev_end:
                                has_common = True
                            else:
                                has_different = True

                        if has_common and has_different:
                            profit = prefix_sums[day][end] - prefix_sums[day][start - 1]
                            if profit > best_next_profit:
                                best_next_profit = profit
                                best_next_start = start
                                best_next_end = end

                prev_start, prev_end = best_next_start, best_next_end

        if dp[n - 1] > max_profit:
            max_profit = dp[n - 1]

    return max_profit

