import heapq
from collections import defaultdict

def solve():
    t = int(input())
    for _ in range(t):
        n, m, p = map(int, input().split())
        houses_need_internet = list(map(int, input().split()))

        # Build the graph
        graph = defaultdict(list)
        for _ in range(m):
            u, v, w = map(int, input().split())
            graph[u].append((v, w))
            graph[v].append((u, w))

        # Calculate shortest paths from each house to all other houses
        shortest_paths = {}
        for house in range(1, n+1):
            shortest_paths[house] = dijkstra(graph, house, n)

        # Calculate the latency for each possible k
        result = []
        for k in range(1, n+1):
            if k >= p:
                # If we can place servers in all houses that need internet, latency is 0
                result.append(0)
                continue

            # Calculate the minimum latency for each possible set of k servers
            min_latency = float('inf')

            # For the hard version, we need to optimize the approach
            # We'll use a greedy approach based on clustering

            # Calculate the latency matrix between houses that need internet
            latency_matrix = []
            for i in range(p):
                house_i = houses_need_internet[i]
                row = []
                for j in range(p):
                    house_j = houses_need_internet[j]
                    row.append(shortest_paths[house_i][house_j])
                latency_matrix.append(row)

            # Use a greedy clustering approach
            min_latency = greedy_clustering(latency_matrix, p, k)
            result.append(min_latency)

        print(*result)

def dijkstra(graph, start, n):
    distances = {i: float('inf') for i in range(1, n+1)}
    distances[start] = 0
    priority_queue = [(0, start)]

    while priority_queue:
        current_distance, current_node = heapq.heappop(priority_queue)

        if current_distance > distances[current_node]:
            continue

        for neighbor, weight in graph[current_node]:
            distance = max(current_distance, weight)  # Maximum latency along the path

            if distance < distances[neighbor]:
                distances[neighbor] = distance
                heapq.heappush(priority_queue, (distance, neighbor))

    return distances

def greedy_clustering(latency_matrix, p, k):
    # Initialize each house as its own cluster
    clusters = [[i] for i in range(p)]

    # Calculate the cost of merging each pair of clusters
    while len(clusters) > k:
        min_cost = float('inf')
        merge_i, merge_j = -1, -1

        for i in range(len(clusters)):
            for j in range(i+1, len(clusters)):
                cost = calculate_merge_cost(latency_matrix, clusters[i], clusters[j])
                if cost < min_cost:
                    min_cost = cost
                    merge_i, merge_j = i, j

        # Merge the two clusters with the minimum cost
        clusters[merge_i].extend(clusters[merge_j])
        clusters.pop(merge_j)

    # Calculate the total latency
    total_latency = 0
    for cluster in clusters:
        for house in cluster:
            min_latency = float('inf')
            for potential_server in cluster:
                if house != potential_server:
                    min_latency = min(min_latency, latency_matrix[house][potential_server])
            if min_latency != float('inf'):
                total_latency += min_latency

    return total_latency

def calculate_merge_cost(latency_matrix, cluster1, cluster2):
    cost = 0
    for house1 in cluster1:
        min_latency = float('inf')
        for house2 in cluster2:
            min_latency = min(min_latency, latency_matrix[house1][house2])
        if min_latency != float('inf'):
            cost += min_latency

    for house2 in cluster2:
        min_latency = float('inf')
        for house1 in cluster1:
            min_latency = min(min_latency, latency_matrix[house2][house1])
        if min_latency != float('inf'):
            cost += min_latency

    return cost

