    freq = {}
    for char in s:
        freq[char] = freq.get(char, 0) + 1

    # If there's only one character, no need to change
    if len(freq) == 1:
        print(''.join(s))
        return

    # Find the character with the highest frequency
    max_char = max(freq, key=freq.get)

    # If there's a tie, choose the lexicographically smaller character
    max_freq = freq[max_char]
    for char in freq:
        if freq[char] == max_freq and char < max_char:
            max_char = char

    # Find a character with the lowest frequency to replace
    min_char = min(freq, key=freq.get)

    # If there's a tie, choose the lexicographically larger character
    min_freq = freq[min_char]
    for char in freq:
        if freq[char] == min_freq and char > min_char:
            min_char = char

    # Replace one occurrence of min_char with max_char
    for i in range(n):
        if s[i] == min_char:
            s[i] = max_char
            break

    print(''.join(s))

