                        new_dp[s + 1][i] = max(new_dp[s + 1][i], dp[s][i])

                    # Increase intelligence
                    if i + 1 <= m:
                        new_dp[s][i + 1] = max(new_dp[s][i + 1], dp[s][i])

            dp = new_dp

