            for s in range(m + 1):
                for i in range(m + 1):
                    if dp[s][i] == -1:
                        continue

                    if i >= record:  # Pass the check
                        dp[s][i] = max(dp[s][i], dp[s][i] + 1)

        else:  # Strength check
            for s in range(m + 1):
                for i in range(m + 1):
                    if dp[s][i] == -1:
