def is_tree(graph, n):
    visited = [False] * (n+1)

    def dfs(node, parent):
        visited[node] = True
        for neighbor in graph[node]:
            if neighbor == parent:
                continue
            if visited[neighbor]:
                return False
            if not dfs(neighbor, node):
                return False
        return True

    if not dfs(1, -1):
        return False

    # Check if all vertices are visited
    for i in range(1, n+1):
        if not visited[i]:
            return False

    return True

