            if star_x == x and star_y < y and (up is None or star_y > up):
                up = star_y
            if star_x == x and star_y > y and (down is None or star_y < down):
                down = star_y

        if up is not None:
            connections += 1
        if down is not None:
            connections += 1

        return connections >= 3

    def min_galaxies(stars):
        if not stars:
            return 0

        # Try adding stars at empty positions
        n = max(max(x for x, _ in stars), max(y for _, y in stars)) + 1
        while True:
            added = False
            for x in range(n):
                for y in range(n):
                    if (x, y) not in stars and can_add_star(x, y, stars):
                        stars.add((x, y))
                        added = True
            if not added:
                break

        return count_galaxies(stars)

    def process_test_case():
        n = int(input())
        matrix = []
        for _ in range(n):
            matrix.append(input())

        # Find all stars
        all_stars = set()
        for i in range(n):
            for j in range(n):
                if matrix[i][j] == '1':
                    all_stars.add((i, j))

        # Calculate the sum of values for all non-empty subsets
        total_sum = 0

        # Generate all non-empty subsets using binary representation
        for mask in range(1, 1 << len(all_stars)):
            subset = set()
            for i, star in enumerate(all_stars):
                if mask & (1 << i):
                    subset.add(star)

            total_sum = (total_sum + min_galaxies(subset)) % MOD

        return total_sum

    t = int(input())
    for _ in range(t):
        print(process_test_case())

