        visited = [False] * n
        components = []

        for i in range(n):
            if not visited[i]:
                component = []
                queue = [i]
                visited[i] = True

                while queue:
                    node = queue.pop(0)
                    component.append(node)

                    for neighbor in graph[node]:
                        if not visited[neighbor]:
                            visited[neighbor] = True
                            queue.append(neighbor)

                components.append(component)

        # Check if each component is a clique (all segments intersect)
        cliques = []
        for comp in components:
            is_clique = True
            for i in range(len(comp)):
