def solve(n, p):
    # Initialize the result array for counts of arrays with k starting cities
    result = [0] * (n + 1)

    # Base cases
    if n == 1:
        result[1] = 1  # Only one array [1] with 1 starting city
        return result

    # Calculate binomial coefficients modulo p
    def binomial(n, k, p):
        if k < 0 or k > n:
            return 0
        if k == 0 or k == n:
            return 1

        res = 1
        for i in range(k):
            res = (res * (n - i)) % p
            res = (res * pow(i + 1, p - 2, p)) % p
        return res

    # Calculate powers of n modulo p
    powers_of_n = [1]
    for i in range(1, n + 1):
        powers_of_n.append((powers_of_n[-1] * n) % p)

    # Calculate the number of arrays for each k
    for k in range(n + 1):
        for j in range(k + 1):
            term = (binomial(k, j, p) * binomial(n - 1, k - j, p)) % p
            term = (term * powers_of_n[n - k]) % p
            if j % 2 == 0:
                result[k] = (result[k] + term) % p
            else:
                result[k] = (result[k] - term + p) % p

    return result

