            graph[u].append((v, l1, l2))
            graph[v].append((u, l1, l2))

        # Dijkstra to find shortest path from n to all other nodes using bus
        dist_bus = [float('inf')] * (n+1)
        dist_bus[n] = 0
        pq = [(0, n)]

        while pq:
            dist, node = heapq.heappop(pq)
            if dist > dist_bus[node]:
                continue

            for neighbor, bus_time, _ in graph[node]:
                if dist_bus[neighbor] > dist + bus_time:
                    dist_bus[neighbor] = dist + bus_time
                    heapq.heappush(pq, (dist_bus[neighbor], neighbor))

        # Dijkstra to find shortest path from n to all other nodes using walking
        dist_walk = [float('inf')] * (n+1)
        dist_walk[n] = 0
        pq = [(0, n)]

        while pq:
            dist, node = heapq.heappop(pq)
            if dist > dist_walk[node]:
                continue

            for neighbor, _, walk_time in graph[node]:
                if dist_walk[neighbor] > dist + walk_time:
                    dist_walk[neighbor] = dist + walk_time
                    heapq.heappush(pq, (dist_walk[neighbor], neighbor))

        # Dijkstra to find shortest path from 1 to all other nodes using bus
        dist_from_1_bus = [float('inf')] * (n+1)
        dist_from_1_bus[1] = 0
        pq = [(0, 1)]

        while pq:
            dist, node = heapq.heappop(pq)
            if dist > dist_from_1_bus[node]:
                continue

            for neighbor, bus_time, _ in graph[node]:
                if dist_from_1_bus[neighbor] > dist + bus_time:
                    dist_from_1_bus[neighbor] = dist + bus_time
                    heapq.heappush(pq, (dist_from_1_bus[neighbor], neighbor))

        # Dijkstra to find shortest path from 1 to all other nodes using walking
        dist_from_1_walk = [float('inf')] * (n+1)
        dist_from_1_walk[1] = 0
        pq = [(0, 1)]

        while pq:
            dist, node = heapq.heappop(pq)
            if dist > dist_from_1_walk[node]:
                continue

            for neighbor, _, walk_time in graph[node]:
                if dist_from_1_walk[neighbor] > dist + walk_time:
                    dist_from_1_walk[neighbor] = dist + walk_time
                    heapq.heappush(pq, (dist_from_1_walk[neighbor], neighbor))

        # Calculate the latest time to leave home
        latest_time = -1

        # Case 1: Leave home, walk during call, then take bus to destination
        for i in range(1, n+1):
            # Time to reach node i from home before call
            time_to_i = dist_from_1_bus[i]
            if time_to_i <= t1:
                # Time to reach destination from i after call
                time_from_i = dist_bus[i]
                if t2 + time_from_i <= t0:
                    latest_time = max(latest_time, t1 - time_to_i)

        # Case 2: Leave home, walk during call, continue walking to destination
        for i in range(1, n+1):
            # Time to reach node i from home before call
            time_to_i = dist_from_1_bus[i]
            if time_to_i <= t1:
                # Time to reach destination from i by walking
                time_from_i = dist_walk[i]
                if t1 + time_from_i <= t0:
                    latest_time = max(latest_time, t1 - time_to_i)

        # Case 3: Stay at home during call, then take bus to destination
        if t2 + dist_bus[1] <= t0:
