import heapq
from collections import defaultdict, deque

def solve():
    t = int(input())
    for _ in range(t):
        n, m, q = map(int, input().split())

        # Build the graph
        graph = defaultdict(list)
        for _ in range(m):
            u, v, w = map(int, input().split())
            graph[u].append((v, w))
            graph[v].append((u, w))

        results = []
        for _ in range(q):
            a, b, k = map(int, input().split())
            results.append(find_kth_max_weight(graph, a, b, k, n))

        print(*results)

def find_kth_max_weight(graph, start, end, k, n):
    # Binary search on the weight threshold
    left, right = 1, 10**9

    while left <= right:
        mid = (left + right) // 2

        # Check if there's a path from start to end with all edges <= mid
        # and the path has at least k edges
        if has_path_with_max_weight(graph, start, end, mid, k, n):
            right = mid - 1
        else:
            left = mid + 1

    return left

def has_path_with_max_weight(graph, start, end, max_weight, k, n):
    # BFS to find if there's a path from start to end with all edges <= max_weight
    # and the path has at least k edges
    visited = set()
    queue = deque([(start, 0)])  # (node, edge_count)

    while queue:
        node, edge_count = queue.popleft()

        if node == end and edge_count >= k:
            return True

        if node in visited:
            continue

        visited.add(node)

        for neighbor, weight in graph[node]:
            if weight <= max_weight and neighbor not in visited:
                queue.append((neighbor, edge_count + 1))

    return False

def find_kth_max_weight(graph, start, end, k, n):
    # Use Dijkstra's algorithm with a custom comparison
    pq = [(0, start, [])]  # (max_edge_weight, node, path_edges)
    visited = set()

    while pq:
        max_edge, node, edges = heapq.heappop(pq)

        if node == end:
            # Sort edges in non-increasing order
            edges.sort(reverse=True)
            if len(edges) >= k:
                return edges[k-1]

        if node in visited:
            continue

        visited.add(node)

        for neighbor, weight in graph[node]:
            if neighbor not in visited:
                new_edges = edges.copy()
                new_edges.append(weight)
                # Negate max_edge for min-heap to act as max-heap
                heapq.heappush(pq, (max_edge, neighbor, new_edges))

    return -1  # Should not reach here if the problem guarantees a path with k edges

def find_kth_max_weight(graph, start, end, k, n):
    # Use modified Dijkstra's algorithm to find all paths
    # and then select the one with the smallest k-th maximum weight

    # Initialize distances with infinity
    dist = {}
    for i in range(1, n+1):
        for j in range(k+1):
            dist[(i, j)] = float('inf')

    # Priority queue for Dijkstra
    pq = [(0, start, 0)]  # (current_kth_max, node, edges_used)

    while pq:
        current_kth_max, node, edges_used = heapq.heappop(pq)

