def solve():
    n, c, m = map(int, input().split())
    a = list(map(int, input().split()))

    MOD = 10**9 + 7

    # Count the number of airplanes thrown by each floor
    count = [0] * (n + 1)
    for floor in a:
        if floor > 0:
            count[floor] += 1

    # Count the number of gaps
    gaps = a.count(0)

    # Initialize DP array
    # dp[i][j][k] = number of ways to fill in i gaps such that
    # j airplanes have been thrown in total
    # k floors have thrown at least c airplanes
    dp = [[[0 for _ in range(n + 1)] for _ in range(m + 1)] for _ in range(gaps + 1)]
    dp[0][0][0] = 1

    # Process the known airplanes first
    visible = [0] * (n + 1)  # visible[i] = number of airplanes visible to floor i

    for idx, floor in enumerate(a):
        if floor > 0:
            # Update visibility for all floors that can see this airplane
            for i in range(1, floor + 1):
                visible[i] += 1

    # Count how many floors have already seen at least c airplanes
    satisfied = 0
    for i in range(1, n + 1):
        if visible[i] >= c:
            satisfied += 1

    dp[0][sum(count)][satisfied] = 1

    # Fill in the gaps
    for g in range(gaps):
        for thrown in range(m + 1):
            for sat in range(n + 1):
                if dp[g][thrown][sat] == 0:
                    continue

                # Try each floor for the next gap
                for floor in range(1, n + 1):
                    # Skip if this floor has already seen c airplanes
                    if visible[floor] >= c:
                        continue

                    # Update visibility
                    new_visible = visible.copy()
                    for i in range(1, floor + 1):
                        new_visible[i] += 1

                    # Count newly satisfied floors
                    new_sat = sat
                    for i in range(1, n + 1):
                        if visible[i] < c and new_visible[i] >= c:
                            new_sat += 1

                    dp[g + 1][thrown + 1][new_sat] = (dp[g + 1][thrown + 1][new_sat] + dp[g][thrown][sat]) % MOD

    # The answer is the number of ways to fill all gaps such that all floors have seen at least c airplanes
    return dp[gaps][m][n]

