    return right

def can_build(k, w, b):
    # Calculate the total number of pins needed for a frame of side length k
    total_pins_needed = k * (k + 1) // 2

    # If we don't have enough pins in total, we can't build the frame
    if w + b < total_pins_needed:
        return False

    # Try all possible ways to assign colors to rows
    for white_rows in range(k + 1):
        # Calculate the number of white and black pins needed
        white_pins_needed = sum(i for i in range(1, k + 1) if i <= white_rows)
        black_pins_needed = total_pins_needed - white_pins_needed

        # Check if we have enough pins of each color
        if white_pins_needed <= w and black_pins_needed <= b:
            return True

    return False

