    # Initialize matching
    max_match = 0
    match_y = [-1] * n_cols

    for root in range(n_rows):
        # If already matched, skip
        if any(match_y[j] == root for j in range(n_cols)):
            continue

        # Initialize augmenting path
        queue = [root]
        parent = [-1] * n_rows
        visited_x = [False] * n_rows
        visited_y = [False] * n_cols
        visited_x[root] = True

        # Find an augmenting path
        while queue and match_y.count(-1) > 0:
            x = queue.pop(0)
            for y in range(n_cols):
                if not visited_y[y] and cost_matrix[x][y] == lx[x] + ly[y]:
                    visited_y[y] = True
                    if match_y[y] == -1:
                        # Found an augmenting path
                        while x != -1:
                            match_y[y] = x
                            temp = parent[x]
                            parent[x] = y
                            y = temp
                            x = parent[x] if y != -1 else -1
                        max_match += 1
                        break
                    else:
                        # Continue BFS
                        queue.append(match_y[y])
                        parent[match_y[y]] = y
                        visited_x[match_y[y]] = True

            if max_match == min(n_rows, n_cols):
                break

        if max_match == min(n_rows, n_cols):
            break

        # Update dual variables
        delta = float('inf')
        for i in range(n_rows):
            if visited_x[i]:
                for j in range(n_cols):
                    if not visited_y[j]:
                        delta = min(delta, cost_matrix[i][j] - lx[i] - ly[j])

        for i in range(n_rows):
            if visited_x[i]:
                lx[i] += delta

        for j in range(n_cols):
            if visited_y[j]:
                ly[j] -= delta

    # Calculate the minimum cost
    min_cost = sum(cost_matrix[match_y[j]][j] for j in range(n_cols) if match_y[j] != -1)
    return min_cost

