from collections import deque

def solve(n, m, maze):
    # Find start and target positions
    start, target = None, None
    for i in range(n):
        for j in range(m):
            if maze[i][j] == 'S':
                start = (i, j)
            elif maze[i][j] == 'T':
                target = (i, j)

    # Directions: right, down, left, up
    directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]

    # BFS with state (row, col, direction, consecutive_steps, total_steps)
    queue = deque([(start[0], start[1], -1, 0, 0)])
    # visited[row][col][direction][consecutive_steps]
    visited = set()

    while queue:
        row, col, dir_idx, consecutive, steps = queue.popleft()

        if (row, col) == target:
            return steps

        state = (row, col, dir_idx, consecutive)
        if state in visited:
            continue
        visited.add(state)

        for new_dir_idx, (dr, dc) in enumerate(directions):
            new_row, new_col = row + dr, col + dc

            # Check if the new position is valid
            if 0 <= new_row < n and 0 <= new_col < m and maze[new_row][new_col] != '#':
                new_consecutive = consecutive + 1 if new_dir_idx == dir_idx else 1

                # Check if we can make this move (not more than 3 consecutive steps in same direction)
                if new_consecutive <= 3:
                    queue.append((new_row, new_col, new_dir_idx, new_consecutive, steps + 1))

    return -1

def main():
    n, m = map(int, input().split())
    maze = [input() for _ in range(n)]
    print(solve(n, m, maze))

if __name__ == "__main__":
    main()
