def is_prime(n):
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

def solve(l, r):
    MOD = 998244353

    # Find all primes in the range [l+l, r+r]
    max_val = r + r
    primes = []
    for i in range(2, max_val + 1):
        if is_prime(i):
            primes.append(i)

    # Initialize DP array
    dp = {}

    def dfs(left, right):
        if (left, right) in dp:
            return dp[(left, right)]

        # Try LTR operation
        ltr_candidates = []
        for p in primes:
            if p - left > left and p - left <= right:
                ltr_candidates.append(p - left)

        # Try RTL operation
        rtl_candidates = []
        for p in primes:
            if p - right >= left and p - right < right:
                rtl_candidates.append(p - right)

        # If no operations are possible, this is a terminal state
        if not ltr_candidates and not rtl_candidates:
            dp[(left, right)] = (1, right - left)
            return dp[(left, right)]

        # Try LTR operation with the largest x
        min_length = float('inf')
        count = 0

        if ltr_candidates:
            x = max(ltr_candidates)
            new_left = (left + x) / 2
            ltr_count, ltr_length = dfs(new_left, right)
            if ltr_length < min_length:
                min_length = ltr_length
                count = ltr_count
            elif ltr_length == min_length:
                count = (count + ltr_count) % MOD

        # Try RTL operation with the smallest x
        if rtl_candidates:
            x = min(rtl_candidates)
            new_right = (right + x) / 2
            rtl_count, rtl_length = dfs(left, new_right)
            if rtl_length < min_length:
                min_length = rtl_length
                count = rtl_count
            elif rtl_length == min_length:
                count = (count + rtl_count) % MOD

        dp[(left, right)] = (count, min_length)
        return dp[(left, right)]

    result, _ = dfs(l, r)
    return result

def main():
    t = int(input())
    for _ in range(t):
        l, r = map(int, input().split())
        print(solve(l, r))

if __name__ == "__main__":
    main()
