import heapq

def solve():
    R, C, X = map(int, input().split())
    grid = []
    for _ in range(R):
        grid.append([int(c) for c in input().strip()])

    Q = int(input())

    for _ in range(Q):
        Rs, Cs, Rf, Cf = map(int, input().split())
        Rs -= 1  # Convert to 0-indexed
        Cs -= 1
        Rf -= 1
        Cf -= 1

        # Dijkstra's algorithm
        dist = [[float('inf')] * C for _ in range(R)]
        dist[Rs][Cs] = 0
        pq = [(0, Rs, Cs)]  # (penalty, row, col)

        # Directions: up, right, down, left
        dr = [-1, 0, 1, 0]
        dc = [0, 1, 0, -1]

        visited = set()

        while pq:
            penalty, r, c = heapq.heappop(pq)

            if (r, c) in visited:
                continue

            visited.add((r, c))

            if r == Rf and c == Cf:
                break

            for i in range(4):
                nr, nc = r + dr[i], c + dc[i]
                if 0 <= nr < R and 0 <= nc < C:
                    h1 = grid[r][c]
                    h2 = grid[nr][nc]
                    new_penalty = penalty + (h1 - h2) ** X

                    if new_penalty < dist[nr][nc]:
                        dist[nr][nc] = new_penalty
                        heapq.heappush(pq, (new_penalty, nr, nc))

        # Check for negative cycles
        # For odd X, if there's a cycle with a height difference, we can get arbitrarily small penalty
        # We'll use Bellman-Ford to detect negative cycles

        # Initialize distances for Bellman-Ford
        bf_dist = [[float('inf')] * C for _ in range(R)]
        bf_dist[Rs][Cs] = 0

        # Relax all edges |V|-1 times
        for _ in range(R * C - 1):
            for r in range(R):
                for c in range(C):
                    if bf_dist[r][c] == float('inf'):
                        continue

                    for i in range(4):
                        nr, nc = r + dr[i], c + dc[i]
                        if 0 <= nr < R and 0 <= nc < C:
                            h1 = grid[r][c]
                            h2 = grid[nr][nc]
                            new_penalty = bf_dist[r][c] + (h1 - h2) ** X

                            if new_penalty < bf_dist[nr][nc]:
                                bf_dist[nr][nc] = new_penalty

        # Check for negative cycles
        has_negative_cycle = False
        for r in range(R):
            for c in range(C):
                if bf_dist[r][c] == float('inf'):
                    continue

                for i in range(4):
                    nr, nc = r + dr[i], c + dc[i]
                    if 0 <= nr < R and 0 <= nc < C:
                        h1 = grid[r][c]
                        h2 = grid[nr][nc]
                        if bf_dist[r][c] + (h1 - h2) ** X < bf_dist[nr][nc]:
                            has_negative_cycle = True
                            break

                if has_negative_cycle:
                    break

            if has_negative_cycle:
                break

        # Check if there's a path from start to destination
        if has_negative_cycle:
            # Check if the negative cycle is reachable from start and can reach destination
            if dist[Rf][Cf] != float('inf'):
                print("INVALID")
