def solve(k):
    if k == 0:
        return [2, 1, [(1, 2)]]

    # Find the largest power of 2 less than or equal to k
    p = 1
    while p * 2 <= k:
        p *= 2

    # If k is a power of 2, we can construct a complete graph with k+1 nodes
    if k == p:
        n = k + 1
        edges = []
        for i in range(1, n+1):
            for j in range(i+1, n+1):
                edges.append((i, j))
        return [n, len(edges), edges]

    # If k is not a power of 2, we need to check if it can be represented as 2^a + 2^b
    remaining = k - p
    q = 1
    while q <= remaining:
        if q == remaining:
            # We can construct a graph with p+1 nodes and add q more nodes
            n = p + q + 1
            edges = []
            # Complete graph on first p+1 nodes
            for i in range(1, p+2):
                for j in range(i+1, p+2):
                    edges.append((i, j))
            # Connect the remaining q nodes to node 1
            for i in range(p+2, n+1):
                edges.append((1, i))
            return [n, len(edges), edges]
        q *= 2

    # If we can't represent k as 2^a + 2^b, it's impossible
    return [-1, -1, []]

