        for i in range(n):
            prefix_sum.append(prefix_sum[-1] + a[i])

        # dp[i][j] = (min cost to remove first i elements with k=j, number of ways)
        dp = [[(float('inf'), 0) for _ in range(m+1)] for _ in range(n+1)]
        dp[0][1] = (0, 1)  # Base case: no elements removed, k=1, cost=0, 1 way

        for i in range(n+1):
            for j in range(1, m+1):
                if dp[i][j][0] == float('inf'):
                    continue

                # Option 1: Increase k if possible
                if j < m:
                    if dp[i][j+1][0] > dp[i][j][0]:
                        dp[i][j+1] = (dp[i][j][0], dp[i][j][1])
                    elif dp[i][j+1][0] == dp[i][j][0]:
                        dp[i][j+1] = (dp[i][j+1][0], (dp[i][j+1][1] + dp[i][j][1]) % MOD)

                # Option 2: Remove prefix
                for l in range(1, n-i+1):
                    if prefix_sum[i+l] - prefix_sum[i] <= b[j]:
                        new_cost = dp[i][j][0] + (m - j)
                        if dp[i+l][j][0] > new_cost:
                            dp[i+l][j] = (new_cost, dp[i][j][1])
                        elif dp[i+l][j][0] == new_cost:
                            dp[i+l][j] = (new_cost, (dp[i+l][j][1] + dp[i][j][1]) % MOD)

        # Find minimum cost among all possible k values for removing all elements
        min_cost = float('inf')
        total_ways = 0

        for j in range(1, m+1):
            if dp[n][j][0] < min_cost:
                min_cost = dp[n][j][0]
                total_ways = dp[n][j][1]
            elif dp[n][j][0] == min_cost:
                total_ways = (total_ways + dp[n][j][1]) % MOD

