            print("Bob")
        else:
            print("Alice")

def calculate_grundy(x, a):
    # For each pile, we need to calculate its Grundy number
    # We can use a memoization approach to avoid recalculating
    memo = {}
    return get_grundy(x, a, memo)

def get_grundy(x, a, memo):
    if x in memo:
        return memo[x]

    # If no valid moves, Grundy number is 0
    if x == 0:
        return 0

    # Set of Grundy numbers of all possible next states
    next_grundy = set()

    # Try all possible moves
    for d in range(1, min(a, x) + 1):
        # Check if the move is valid: (x & d) == d
        if (x & d) == d:
            # Calculate Grundy number of the next state
            next_grundy.add(get_grundy(x - d, a, memo))

    # Find the minimum excludant (mex)
    grundy = 0
    while grundy in next_grundy:
        grundy += 1

    memo[x] = grundy
    return grundy

solve()
