    if (a | b) - (a & c) == d:
        return a

    # Try a few more values
    for a in range(1, 1000):
        if (a | b) - (a & c) == d:
            return a

    # If we can't find a solution quickly, try a specific pattern
    a = b ^ c
    if (a | b) - (a & c) == d:
