    MOD = 998244353

    # Find all possible operations
    operations = []

    # Try LTR operations
    for x in range(r, l, -1):
        if is_prime(l + x):
            operations.append(("LTR", (l + x) / 2, r))
            break

    # Try RTL operations
    for x in range(l, r):
        if is_prime(r + x):
            operations.append(("RTL", l, (r + x) / 2))
            break

    # If no operations possible, return 1
    if not operations:
        return 1

    # Count ways to get minimum length
    min_length = float('inf')
    count = 0

    for op, new_l, new_r in operations:
        length = new_r - new_l
        if length < min_length:
            min_length = length
            count = 1
        elif length == min_length:
            count += 1

    return count % MOD

